import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import "../components/dashboard.css";
import logo from '../components/images/logo-png.png';
import React, { useState, useEffect, useContext } from 'react';
import { AuthContext } from '../components/AuthContext';
import axios from 'axios';

function DashBoard() {
    const [rows, setRows] = useState([{ itemName:'', unit: '', price: '', amount: '' }]);
    const [total, setTotal] = useState(0);
    const [invoices, setInvoices] = useState([]);
    const { user } = useContext(AuthContext);
    const [view, setView] = useState('dashboard');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [address, setAddress] = useState('');
    const [city, setCity] = useState('');
    const [state, setState] = useState('');
    const [pincode, setPincode] = useState('');
    const [email, setEmailId] = useState('');
    const [phone, setPhone] = useState('');
    const [emailerror, setEmailError] = useState('');
    const emailPattern = /^[A-Z0-9. _%+-]+@[A-Z0-9. -]+\.[A-Z0-9-.]+$/;
    const [customername, setcustomername] = useState([]);
    const [selectedddValue, setSelectedddValue] = useState('Select Customer');
    const [inv_customername, setinv_customername] = useState('');
    const [inv_itemname, setinv_itemname] = useState('');
    const [inv_unit, setinv_unit] = useState('');
    const [inv_price, setinv_price] = useState('');
    const [inv_tlt_amt, setinv_tlt_amt] = useState('');
    const [itemdetails, setitemDetails] = ([{ id: 1, itemName: '', quantity: 0, price: 0, amount: 0 }]);
    // invoice creation start
    
    const [tableData, setTableData] = useState([
        { id: 1, itemName: '', unit: '', price: '', amount: '' }
    ]);

    const addNewRow = () => {
        const newRow = { id: 1, itemName: '', unit: '', price: '', amount: '' };
        setTableData([...tableData, newRow]);
    };
    const handleInputChange = (id, field, value) => {
        const updatedData = tableData.map(row => {
            if(row.id===id){
                let updatedrow={...row,[field]:value};
                if(field==="unit" || field==="price"){
                    const quantity=field==='unit'?value:row.unit;
                    const price=field==='price'?value:row.price;
                    updatedrow.amount=quantity && price?(quantity*price).toFixed(2):'';

                }
                return updatedrow;
            }
            return row;
        });
        setTableData(updatedData);
    };

    const deleteRow = (id) => {
        setTableData(tableData.filter(row => row.id !== id));
    };

    // const updateTotal = (rows) => {
    //     const totalSum = rows.reduce((sum, row) => sum + (row.amount *row.unit || 0), 0);
    //     setTotal(totalSum);
    // };

    

    // useEffect(() => {
    //     updateTotal(rows);
    // }, [rows]);
    //invoice creatiuon end


    // get invoice data for view dashboard start
    const getInvoiceData = () => {
        // Replace 'YOUR_API_ENDPOINT' with your actual API endpoint
        axios.get('https://localhost:7116/api/controller/GetInvoicesByGSTNo', {
            headers: {
                Authorization: `Bearer ${user.token}`
            }
        })
            .then(response => {
                setInvoices(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the data!', error);
            });
    }
    useEffect(() => {
        // Replace 'YOUR_API_ENDPOINT' with your actual API endpoint
        // axios.get('https://localhost:7116/api/controller/GetInvoicesByGSTNo', {
        //     headers: {
        //         Authorization: `Bearer ${user.token}`
        //     }
        // })
        //     .then(response => {
        //         setInvoices(response.data);
        //     })
        //     .catch(error => {
        //         console.error('There was an error fetching the data!', error);
        //     });
        getInvoiceData();
    }, [user.token]);

    window.addEventListener("beforeunload", (event) => {
        getInvoiceData();
    })
    window.addEventListener("unload", (event) => {
        getInvoiceData();
    })

    const renderInvoices = () => {
        if (!invoices) {
            return (
                <tr>
                    <td colSpan="4">No record found</td>
                </tr>
            );
        }

        if (!invoices.success || !invoices.msg) {
            return (
                <tr>
                    <td colSpan="4">No record found</td>
                </tr>
            );
        }

        return invoices.msg.map((invoice, index) => {
            const invoiceDate = new Date(invoice.invoiceDate);
            const formattedDate = invoiceDate.toLocaleDateString('en-US', {
                year: 'numeric',
                month: 'short',
                day: 'numeric',
            });

            return (
                <tr key={index}>
                    <td>{formattedDate}</td>
                    <td>{invoice.customerName}</td>
                    <td>{"INV" + invoice.invoiceNo}</td>
                    <td>${invoice.amount.toFixed(2)}</td>
                    <td class="text-end">
                        <button type="button" class="btn btn-sm btn-square btn-danger">
                            <i class="bi bi-trash"></i>
                        </button>
                    </td>
                </tr>
            );
        });
    };
    // get invoice data for view dashboard end

    //side nav start
    const handleViewChange = (view) => {
        setView(view);
    };
    //side nav end

    //populate invoice customername dropdown start

    const handleSelect = (value) => {
        setSelectedddValue(value);
    };

    const getCustomerName = () => {
        // Replace 'YOUR_API_ENDPOINT' with your actual API endpoint
        axios.get('https://localhost:7116/api/controller/GetCustomerNameByGstNo', {
            headers: {
                Authorization: `Bearer ${user.token}`
            }
        })
            .then(response => {
                setcustomername(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the data!', error);
            });
    }
    useEffect(() => {
        getCustomerName();
    }, [user.token]);

    const rendercustomerName = () => {
        if (!customername) {
            return (
                <li>No Record</li>
            );
        }

        if (!customername.success || !customername.msg) {
            return (
                <li>No Record</li>
            );
        }

        return customername.msg.map((customer_name, index) => {
            return (
                <li><a href="#" key={index} class="dropdown-item" onClick={() => handleSelect(customer_name)}>{customer_name}</a>
                    
                </li>
            );
        });
    };
    //populate invoice customername dropdown end

    //customer creation start
    const sbt_customerdata = async (event) => {

        event.preventDefault();
        //setLoading(true);

        // Simple email validation regex

        // if (!emailPattern.test(email)) {
        //     setEmailError('Invalid email address');
        //     return;
        // } else {
        //     setEmailError('');
        try {
            //https://localhost:7116/api/controller/login?gstNo=GST_12345
            const response = await axios.post('https://localhost:7116/api/controller/CreateCustomer', {
                FirstName: firstName,
                LastName: lastName,
                AddressLine: address,
                States: state,
                City: city,
                EmailId: email,
                Pincode: pincode,
                Phone: phone,
            });

            if (response.status === 200) {
                // Set token in Authorization header for all subsequent requests
                axios.defaults.headers.common['Authorization'] = `Bearer ${user.token}`;

                console.log('Customer created successfully');
                setFirstName('');
                setLastName('');
                setAddress('');
                setState('');
                setCity('');
                setPincode('');
                setEmailId('');
                setPhone('');

            } else {
                // Handle login failure
                console.log('Customer creation failed');
                //setShowAlert(true);
            }
        } catch (error) {
            console.error('Error:', error);
            //setShowAlert(true);
        } finally {
            //setLoading(false);
        }
        //}

    };
    //customer creation end

    //invoice creation start
    // const handleItemInputChange = (id, field, value) => {
    //     const updatedData = tableData.map(row => (row.id === id ? { ...row, [field]: value } : row));
    //     setTableData(updatedData);
    // }; 
    // const addNewItemRow = () => {
    //     const newRow = { id: tableData.length + 1, itemName: '', quantity: 0, price: 0, amount: 0 };
    //     setTableData([...tableData, newRow]);
    // };
    // const deleteItemRow = id => {
    //     const updatedData = tableData.filter(row => row.id !== id);
    //     setTableData(updatedData);
    // };

    // const sbt_invoicedata = async (event) => {

    //     event.preventDefault();
    //     const apiData = {
    //         CustomerName: selectedddValue,
    //         Details: tableData.map(({ itemName, unit, price, amount }) => ({
    //             ItemName: itemName,
    //             Quantity: unit,
    //             Price : price,
    //             TotalAmt: amount // Assuming total amount is quantity * price
    //         }))
    //     };
    //     console.log(apiData);
    //     try {
    //         https://localhost:7116/api/controller/login?gstNo=GST_12345
    //         const response = await axios.post('https://localhost:7116/api/controller/CreateInvoice', {
    //           apiData
    //         });

    //         if (response.status === 200) {
    //             Set token in Authorization header for all subsequent requests
    //             axios.defaults.headers.common['Authorization'] = `Bearer ${user.token}`;

    //             console.log('Invoice created successfully');
    //             setFirstName('');
    //             setLastName('');
    //             setAddress('');
    //             setState('');
    //             setCity('');
    //             setPincode('');
    //             setEmailId('');
    //             setPhone('');

    //         } else {
    //             Handle login failure
    //             console.log('Invoice creation failed');
    //             setShowAlert(true);
    //         }
    //     } catch (error) {
    //         console.error('Error:', error);
    //         setShowAlert(true);
    //     } finally {
    //         setLoading(false);
    //     }
    //     }

    // };
    //invocie creation end

    return (
        <div>
            {/* Dashboard */}
            <div className="d-flex flex-column flex-lg-row h-lg-full bg-surface-secondary">
                {/* Vertical Navbar */}
                <nav className="navbar show navbar-vertical h-lg-screen navbar-expand-lg px-0 py-3 navbar-light bg-white border-bottom border-bottom-lg-0 border-end-lg" id="navbarVertical">
                    <div className="container-fluid">
                        {/* Toggler */}
                        <button className="navbar-toggler ms-n2" type="button" data-bs-toggle="collapse" data-bs-target="#sidebarCollapse" aria-controls="sidebarCollapse" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        {/* Brand */}
                        <a className="navbar-brand py-lg-2 mb-lg-5 px-lg-6 me-0" href="#">
                            <img src={logo} alt="" style={{ width: '180px', height: '50px' }} />
                        </a>
                        {/* User menu (mobile) */}
                        <div className="navbar-user d-lg-none">
                            <div className="dropdown">
                                <a href="#" id="sidebarAvatar" role="button" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    <div className="avatar-parent-child">
                                        <img alt="Image Placeholder" src="https://images.unsplash.com/photo-1548142813-c348350df52b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=3&w=256&h=256&q=80" className="avatar avatar- rounded-circle" />
                                        <span className="avatar-child avatar-badge bg-success"></span>
                                    </div>
                                </a>
                                <div className="dropdown-menu dropdown-menu-end" aria-labelledby="sidebarAvatar">
                                    <a href="#" className="dropdown-item">Profile</a>
                                    <a href="#" className="dropdown-item">Settings</a>
                                    <a href="#" className="dropdown-item">Billing</a>
                                    <hr className="dropdown-divider" />
                                    <a href="#" className="dropdown-item">Logout</a>
                                </div>
                            </div>
                        </div>
                        <div className="collapse navbar-collapse" id="sidebarCollapse">
                            <ul className="navbar-nav">
                                <li className="nav-item">
                                    <a className="nav-link" href="#" onClick={() => handleViewChange('dashboard')}>
                                        <i className="bi bi-house"></i> Dashboard
                                    </a>
                                </li>
                                <li className="nav-item">
                                    <a className="nav-link" href="#" onClick={() => handleViewChange('invoice')}>
                                        <i className="bi bi-bar-chart"></i> Invoices
                                    </a>
                                </li>
                                <li className="nav-item">
                                    <a className="nav-link" href="#" onClick={() => handleViewChange('customer')}>
                                        <i className="bi bi-bar-chart"></i> Customer
                                    </a>
                                </li>
                            </ul>
                            <hr className="navbar-divider my-5 opacity-20" />
                            <div className="mt-auto"></div>
                            <ul className="navbar-nav">
                                <li className="nav-item">
                                    <a className="nav-link" href="/login">
                                        <i className="bi bi-box-arrow-left"></i> Logout
                                    </a>
                                </li>
                            </ul>
                        </div>
                    </div>
                </nav>
                {/* Main content and Invoice creation */}
                <div className="h-screen flex-grow-1 overflow-y-lg-auto">
                    <header class="bg-surface-primary border-bottom pt-6">
                        <div class="container-fluid">
                            <div class="mb-npx">
                                <div class="row align-items-center">
                                    <div class="col-sm-6 col-12 mb-4 mb-sm-0">
                                        {/* <!-- Title --> */}
                                        <h1 class="h2 mb-0 ls-tight">Invoice Management</h1>
                                    </div>
                                </div>
                                {/* <!-- Nav --> */}
                                <ul class="nav nav-tabs mt-4 overflow-x border-0">

                                </ul>
                            </div>
                        </div>
                    </header>
                    {view === 'dashboard' && (
                        <main className="py-6 bg-surface-secondary">
                            <div className="container-fluid">
                                <div className="card shadow border-0 mb-7">
                                    <div className="card-header d-flex justify-content-between align-items-center ">
                                        <h5 className="mb-0">Applications</h5>
                                        <div className="d-flex gap-1">
                                            <input type="text" className="form-control form-control-sm" placeholder="Search..." />
                                            <div className="input-group-append">
                                                <button className="btn btn-primary btn-sm">
                                                    <i className="fas fa-search"></i>
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="table-responsive">
                                        <table className="table table-hover table-nowrap">
                                            <thead className="thead-light">
                                                <tr>
                                                    <th scope="col">Invoice Date</th>
                                                    <th scope="col">Customer Name</th>
                                                    <th scope="col">Invoice No</th>
                                                    <th scope="col">Amount</th>
                                                    <th class="text-end">
                                                        <button class="btn btn-sm btn-primary">
                                                            <i class="bi bi-download"></i>
                                                        </button>
                                                    </th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {/* {invoices && invoices.success && invoices.msg.map((invoice, index) => {
                                                const invoiceDate = new Date(invoice.invoiceDate);
                                                const formattedDate = invoiceDate.toLocaleDateString('en-US', {
                                                    year: 'numeric',
                                                    month: 'short',
                                                    day: 'numeric',
                                                });

                                                return (
                                                    <tr key={index}>
                                                        <td>{formattedDate}</td>
                                                        <td>{invoice.customerName}</td>
                                                        <td>{invoice.invoiceNo}</td>
                                                        <td>${invoice.amount.toFixed(2)}</td>
                                                        <td class="text-end"><a href="#" class="btn btn-sm btn-neutral w-20 bg-white h-10">View</a>
                                                    <button type="button" class="btn btn-sm btn-square btn-neutral bg-white  text-danger-hover">
                                                        <i class="bi bi-trash"></i>
                                                    </button></td>
                                                    </tr>
                                                );
                                            })} */}
                                                {renderInvoices()}

                                            </tbody>
                                        </table>
                                    </div>
                                    <div className="card-footer border-0 py-5">
                                        <span className="text-muted text-sm">
                                            {/* Showing {invoices.msg.length} items out of {invoices.msg.length} results found */}
                                            Showing {invoices.msg ? invoices.msg.length : 0} items out of {invoices.msg ? invoices.msg.length : 0} results found
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </main>
                    )}

                    {view === 'invoice' && (
                        <main class="py-6 bg-surface-secondary">
                            <div class="container-fluid">
                                <div class="card shadow border-0 mb-7">
                                    <div class="card-header">
                                        <h5 class="mb-0">Invoice Creation</h5>
                                    </div>
                                    <br></br>
                                    <div className="d-flex align-items-center gap-5">
                                        <div className=" btn-group w-25" style={{ marginLeft: "21px" }}>
                                            <button
                                                className="btn btn-primary btn-sm dropdown-toggle text-dark bg-light border-1 border-black shadow-none "
                                                type="button"
                                                id="defaultDropdown"
                                                data-bs-toggle="dropdown"
                                                data-bs-auto-close="true"
                                                aria-expanded="false"
                                            >
                                                {selectedddValue}
                                            </button>
                                            <ul className="dropdown-menu scrollable-menu " role='menu' aria-labelledby="defaultDropdown" style={{width:"100%"}}>
                                                {rendercustomerName()}
                                            </ul>
                                        </div>
                                        {/* <div class="btn-group">
                                            <button type="button" class="btn btn-primary dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false">
                                                Dropdown Button
                                            </button>
                                            <ul class="dropdown-menu">
                                                <li><a class="dropdown-item" href="#">Action</a></li>
                                                <li><a class="dropdown-item" href="#">Another action</a></li>
                                                <li><hr class="dropdown-divider"/></li>
                                                <li><a class="dropdown-item" href="#">Something else here</a></li>
                                            </ul>
                                        </div> */}

                                        <button
                                            className="btn btn-primary bg-light btn-sm text-dark d-flex align-items-center border-1 border-black shadow-none"
                                            onClick={addNewRow}
                                        >
                                            <i className="bi bi-plus"></i>
                                            <span className="ms-1">Add Row</span>
                                        </button>

                                    </div>

                                    <br></br>
                                    <div class="table-responsive">
                                        <table class="table table-hover table-nowrap">
                                            <thead class="thead-light">
                                                <tr>
                                                    <th scope="col">Item Name</th>
                                                    <th scope="col">Quanity</th>
                                                    <th scope="col">Price</th>
                                                    <th scope="col">Total Amount</th>
                                                    <th></th>
                                                    <th></th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {tableData.map((row) => (
                                                    <tr key={row.id}>
                                                        <td>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                value={row.itemName}
                                                                onChange={(e) => handleInputChange(row.id, 'itemName', e.target.value)}
                                                            />
                                                        </td>
                                                        <td>
                                                            <input
                                                                type="number"
                                                                className="form-control"
                                                                value={row.unit}
                                                                onChange={(e) => handleInputChange(row.id, 'unit', e.target.value)}
                                                            />
                                                        </td>
                                                        <td>
                                                            <input
                                                                type="number"
                                                                className="form-control"
                                                                value={row.price}
                                                                onChange={(e) => handleInputChange(row.id, 'price', e.target.value)}
                                                            />
                                                        </td>
                                                        <td>
                                                            <input
                                                                type="text"
                                                                className="form-control"
                                                                value={row.amount}
                                                                onChange={(e) => handleInputChange(row.id, 'amount', e.target.value)}
                                                            />
                                                        </td>
                                                        <td className="text-end">
                                                        </td>
                                                        <td className="text-end">
                                                            <button className="btn btn-sm btn-square btn-danger " onClick={() => deleteRow(row.id)}>
                                                                <i class="bi bi-trash"></i>
                                                            </button>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>

                                        </table>
                                    </div>
                                    <div class="card-footer border-0 py-5">
                                        <button className="btn btn-primary btn-sm bg-light text-dark d-flex align-items-center border-1 border-black me-2 shadow-none"   >
                                            <span className="ms-1">Submit</span>
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </main>
                    )}
                    {view === 'customer' && (
                        <main class="py-6 bg-surface-secondary">
                            <div class="container-fluid">
                                <div class="card shadow border-0 mb-7">
                                    <div class="card-header">
                                        <h5 class="mb-0">Customer Creation</h5>
                                    </div>
                                    <div class="container py-5">
                                        <form class="p-md-5" onSubmit={sbt_customerdata}>
                                            <div class="btn-group gap-5">
                                                <div class="form-floating mb-3">
                                                    <input type="text" id="txtfname" class="form-control" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                                                    <label class="form-label" for="form4Example1">First Name</label>
                                                </div>
                                                <div class="form-floating mb-3">
                                                    <input type="text" id="txtlname" class="form-control" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                                                    <label class="form-label" for="form4Example2">Last Name</label>
                                                </div>
                                                <div class="form-floating mb-3">
                                                    <input type="text" id="txtphone" class="form-control" value={phone} onChange={(e) => setPhone(e.target.value)} required />
                                                    <label class="form-label" for="form4Example2">Phone No.</label>
                                                </div>
                                            </div>
                                            <div class="form-floating mb-3">
                                                <input type="address" id="txtaddress" class="form-control" value={address} onChange={(e) => setAddress(e.target.value)} required />
                                                <label class="form-label" for="form4Example2">Address</label>
                                            </div>
                                            <div class="btn-group gap-5">
                                                <div class="form-floating mb-3">
                                                    <input type="text" id="txtstate" class="form-control" value={state} onChange={(e) => setState(e.target.value)} required />
                                                    <label class="form-label" for="form4Example2">State</label>
                                                </div>
                                                <div class="form-floating mb-3">
                                                    <input type="text" id="txtcity" class="form-control" value={city} onChange={(e) => setCity(e.target.value)} required />
                                                    <label class="form-label" for="form4Example2">City</label>
                                                </div>
                                            </div>
                                            <div class="btn-group gap-5">
                                                <div class="form-floating mb-3">
                                                    <input type="email" id="txtemail" class="form-control" value={email} onChange={(e) => setEmailId(e.target.value)} required />
                                                    {/* <span style={{color:"red"}}
                                                        {...emailerror}
                                                    /> */}
                                                    <label class="form-label" for="form4Example2">Email Address</label>

                                                </div>
                                                <div class="form-floating mb-3">
                                                    <input type="text" id="txtpincode" class="form-control" value={pincode} onChange={(e) => setPincode(e.target.value)} required />
                                                    <label class="form-label" for="form4Example2">PinCode</label>
                                                </div>
                                            </div>
                                            <div class="card-footer border-0 py-5" style={{ marginLeft: "-20px" }}>
                                                <button className="btn btn-primary btn-sm d-flex align-items-center me-2 bg-light border-1 border-black shadow-none text-dark">
                                                    <span className="ms-1">Submit</span>
                                                </button>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                        </main>
                    )}
                    {/* Main content and Invoice creation end */}
                </div>
            </div>
        </div>
    );
}

export default DashBoard;
