import "../components/dashboard.css";
import React, { useState, useEffect } from 'react';
import "../components/invoices.css";
function DashBoard() {

    const [rows, setRows] = useState([{ id: Date.now(), unit: '', price: '', amount: '' }]);
    const [total, setTotal] = useState(0);

    const addNewRow = () => {
        setRows([...rows, { id: Date.now(), unit: '', price: '', amount: '' }]);
    };

    const handleInputChange = (id, field, value) => {
        const updatedRows = rows.map(row => {
            if (row.id === id) {
                const updatedRow = { ...row, [field]: value };
                if (field === 'unit' || field === 'price') {
                    updatedRow.amount = updatedRow.unit * updatedRow.price;
                }
                return updatedRow;
            }
            return row;
        });
        setRows(updatedRows);
        updateTotal(updatedRows);
    };

    const deleteRow = (id) => {
        const updatedRows = rows.filter(row => row.id !== id);
        setRows(updatedRows);
        updateTotal(updatedRows);
    };

    const updateTotal = (rows) => {
        const totalSum = rows.reduce((sum, row) => sum + (row.amount || 0), 0);
        setTotal(totalSum);
    };

    useEffect(() => {
        updateTotal(rows);
    }, [rows]);

    return (
        <div>

            {/* Dashboard */}
            <div class="d-flex flex-column flex-lg-row h-lg-full bg-surface-secondary">
                {/* Vertical Navbar */}
                <nav class="navbar show navbar-vertical h-lg-screen navbar-expand-lg px-0 py-3 navbar-light bg-white border-bottom border-bottom-lg-0 border-end-lg" id="navbarVertical">
                    <div class="container-fluid">
                        {/* Toggler */}
                        <button class="navbar-toggler ms-n2" type="button" data-bs-toggle="collapse" data-bs-target="#sidebarCollapse" aria-controls="sidebarCollapse" aria-expanded="false" aria-label="Toggle navigation">
                            <span class="navbar-toggler-icon"></span>
                        </button>
                        {/* <!-- Brand --> */}
                        <a class="navbar-brand py-lg-2 mb-lg-5 px-lg-6 me-0" href="#">
                            <img src="https://preview.webpixels.io/web/img/logos/clever-primary.svg" alt="..." />
                        </a>
                        {/* <!-- User menu (mobile) --> */}
                        <div class="navbar-user d-lg-none">
                            {/* <!-- Dropdown --> */}
                            <div class="dropdown">
                                {/* <!-- Toggle --> */}
                                <a href="#" id="sidebarAvatar" role="button" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    <div class="avatar-parent-child">
                                        <img alt="Image Placeholder" src="https://images.unsplash.com/photo-1548142813-c348350df52b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=3&w=256&h=256&q=80" class="avatar avatar- rounded-circle" />
                                        <span class="avatar-child avatar-badge bg-success"></span>
                                    </div>
                                </a>
                                {/* <!-- Menu --> */}
                                <div class="dropdown-menu dropdown-menu-end" aria-labelledby="sidebarAvatar">
                                    <a href="#" class="dropdown-item">Profile</a>
                                    <a href="#" class="dropdown-item">Settings</a>
                                    <a href="#" class="dropdown-item">Billing</a>
                                    <hr class="dropdown-divider" />
                                    <a href="#" class="dropdown-item">Logout</a>
                                </div>
                            </div>
                        </div>
                        {/* <!-- Collapse --> */}
                        <div class="collapse navbar-collapse" id="sidebarCollapse">
                            {/* <!-- Navigation --> */}
                            <ul class="navbar-nav">
                                <li class="nav-item">
                                    <a class="nav-link" href="#">
                                        <i class="bi bi-house"></i> Dashboard
                                    </a>
                                </li>
                                <li class="nav-item">
                                    <a class="nav-link" href="#">
                                        <i class="bi bi-bar-chart"></i> Invoices
                                    </a>
                                </li>

                            </ul>
                            {/* Divider */}
                            <hr class="navbar-divider my-5 opacity-20" />

                            {/* <!-- Push content down --> */}
                            <div class="mt-auto"></div>
                            {/* <!-- User (md) --> */}
                            <ul class="navbar-nav">

                                <li class="nav-item">
                                    <a class="nav-link" href="#">
                                        <i class="bi bi-box-arrow-left"></i> Logout
                                    </a>
                                </li>
                            </ul>
                        </div>
                    </div>
                </nav>
                {/* <!-- Main content --> */}
                <div class="h-screen flex-grow-1 overflow-y-lg-auto" style={{ display: "block" }}>
                    {/* <!-- Header --> */}
                    <header class="bg-surface-primary border-bottom pt-6">
                        <div class="container-fluid">
                            <div class="mb-npx">
                                <div class="row align-items-center">
