import React, { useState} from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import "../components/login.css";
import 'bootstrap/dist/css/bootstrap.min.css';
import loginImg from "../components/login.svg";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Card,
  CardImg,
  Alert
} from "reactstrap";

function Login() {
    const [inputUsername, setInputUsername] = useState("");
    //const [showAlert, setShowAlert] = useState(false)
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    
    const handleSubmit = async (event) => {
    
        event.preventDefault();
        setLoading(true);
    
       
        try {
          //https://localhost:7116/api/controller/login?gstNo=GST_12345
          const response = await axios.post('https://localhost:7116/api/controller/login?gstNo='+inputUsername, {
            //username: inputUsername,
            // password: inputPassword
          });
    
          if (response.status === 200) {
            const { token } = response.data; 
            localStorage.setItem('token', token); // Store the token in localStorage
           navigate('/dashboard');
            console.log('User logged in successfully');
          } else {
            // Handle login failure
            console.log('Login failed');
            //setShowAlert(true);
          }
        } catch (error) {
          console.error('Error:', error);
          //setShowAlert(true);
        } finally {
          setLoading(false);
        }
      };

  return (
    <div className="background">
      <div className="login-box">
        <div className="container">
          <div class="row app-des">
            <div class="col left-background ">
              <h2>Invoice</h2>
              <p>Management</p>
              <CardImg
                className="mobile-img"
                src={loginImg}
                alt="mobile-App"
              />
            </div>
            <div class="col login-form">
              <Form onSubmit={handleSubmit}>
                <h2 className="font-weight-bold mb-4">Login</h2>
                <FormGroup>
                  <Label className="font-weight-bold mb-2">Enter GST Number</Label>
                  <Input 
                    className="mb-3"
                    type="text"
                    placeholder="GST No" onChange={(e) => setInputUsername(e.target.value)} required
                  />
                </FormGroup>
                <Button type="submit" className="mt-3  btn" >
                {loading ? 'Logging In...' : 'Log In'}
                </Button>
                <div className="text-center m-4">Do not have an account? <a href="/register">SignUp</a></div>
              </Form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
