import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import "../components/dashboard.css";
import React, { useState, useEffect, useContext } from 'react';
// import "../components/invoices.css";
import axios from 'axios';

import { AuthContext } from '../components/AuthContext';
function DashBoard() {
    const [rows, setRows] = useState([{ id: Date.now(), unit: '', price: '', amount: '' }]);
    const [total, setTotal] = useState(0);
    const [invoices, setInvoices] = useState([]);
    const { user } = useContext(AuthContext);
    const [view, setView] = useState('dashboard');

    const addNewRow = () => {
        setRows([...rows, { itemName: '', unit: '', price: '', amount: '' }]);
    };

    const handleInputChange = (id, field, value) => {
        const updatedRows = rows.map(row => {
            if (row.id === id) {
                const updatedRow = { ...row, [field]: value };
                if (field === 'unit' || field === 'price') {
                    updatedRow.amount = updatedRow.unit * updatedRow.price;
                }
                return updatedRow;
            }
            return row;
        });
        setRows(updatedRows);
        updateTotal(updatedRows);
    };

    const deleteRow = (id) => {
        const updatedRows = rows.filter(row => row.id !== id);
        setRows(updatedRows);
        updateTotal(updatedRows);
    };

    const updateTotal = (rows) => {
        const totalSum = rows.reduce((sum, row) => sum + (row.amount || 0), 0);
        setTotal(totalSum);
    };

    const [tableData, setTableData] = useState([
        { id: 1, name: '', itemname: '', date: '', company: '', offer: '', meeting: '' }
    ]);

    useEffect(() => {
        updateTotal(rows);
    }, [rows]);

    const getInvoiceData = () => {
        // Replace 'YOUR_API_ENDPOINT' with your actual API endpoint
        axios.get('https://localhost:7116/api/controller/GetInvoicesByGSTNo', {
            headers: {
                Authorization: `Bearer ${user.token}`
            }
        })
            .then(response => {
                setInvoices(response.data);
            })
            .catch(error => {
                console.error('There was an error fetching the data!', error);
            });
    }
    useEffect(() => {
        // Replace 'YOUR_API_ENDPOINT' with your actual API endpoint
        // axios.get('https://localhost:7116/api/controller/GetInvoicesByGSTNo', {
        //     headers: {
        //         Authorization: `Bearer ${user.token}`
        //     }
        // })
        //     .then(response => {
        //         setInvoices(response.data);
        //     })
        //     .catch(error => {
        //         console.error('There was an error fetching the data!', error);
        //     });
        getInvoiceData();
    }, [user.token]);

    window.addEventListener("beforeunload", (event) => {
        getInvoiceData();
    })
    window.addEventListener("unload", (event) => {
        getInvoiceData();
    })

    const renderInvoices = () => {
        if (!invoices) {
            return (
                <tr>
                    <td colSpan="4">No record found</td>
                </tr>
            );
        }

        if (!invoices.success || !invoices.msg) {
            return (
                <tr>
                    <td colSpan="4">No record found</td>
                </tr>
            );
        }

        return invoices.msg.map((invoice, index) => {
            const invoiceDate = new Date(invoice.invoiceDate);
            const formattedDate = invoiceDate.toLocaleDateString('en-US', {
                year: 'numeric',
                month: 'short',
                day: 'numeric',
            });

            return (
                <tr key={index}>
                    <td>{formattedDate}</td>
                    <td>{invoice.customerName}</td>
                    <td>{"INV" + invoice.invoiceNo}</td>
                    <td>${invoice.amount.toFixed(2)}</td>
                </tr>
            );
        });
    };
    const handleViewChange = (view) => {
        setView(view);
    };
    return (
        <div>
            {/* Dashboard */}
            <div className="d-flex flex-column flex-lg-row h-lg-full bg-surface-secondary">
                {/* Vertical Navbar */}
                <nav className="navbar show navbar-vertical h-lg-screen navbar-expand-lg px-0 py-3 navbar-light bg-white border-bottom border-bottom-lg-0 border-end-lg" id="navbarVertical">
                    <div className="container-fluid">
                        {/* Toggler */}
                        <button className="navbar-toggler ms-n2" type="button" data-bs-toggle="collapse" data-bs-target="#sidebarCollapse" aria-controls="sidebarCollapse" aria-expanded="false" aria-label="Toggle navigation">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        {/* Brand */}
                        <a className="navbar-brand py-lg-2 mb-lg-5 px-lg-6 me-0" href="#">
                            <img src="https://preview.webpixels.io/web/img/logos/clever-primary.svg" alt="..." />
                        </a>
                        {/* User menu (mobile) */}
                        <div className="navbar-user d-lg-none">
                            <div className="dropdown">
                                <a href="#" id="sidebarAvatar" role="button" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    <div className="avatar-parent-child">
                                        <img alt="Image Placeholder" src="https://images.unsplash.com/photo-1548142813-c348350df52b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=facearea&facepad=3&w=256&h=256&q=80" className="avatar avatar- rounded-circle" />
                                        <span className="avatar-child avatar-badge bg-success"></span>
                                    </div>
                                </a>
                                <div className="dropdown-menu dropdown-menu-end" aria-labelledby="sidebarAvatar">
                                    <a href="#" className="dropdown-item">Profile</a>
                                    <a href="#" className="dropdown-item">Settings</a>
                                    <a href="#" className="dropdown-item">Billing</a>
                                    <hr className="dropdown-divider" />
                                    <a href="#" className="dropdown-item">Logout</a>
                                </div>
                            </div>
                        </div>
                        <div className="collapse navbar-collapse" id="sidebarCollapse">
                            <ul className="navbar-nav">
                                <li className="nav-item">
                                    <a className="nav-link" href="#" onClick={() => handleViewChange('dashboard')}>
                                        <i className="bi bi-house"></i> Dashboard
                                    </a>
                                </li>
                                <li className="nav-item">
                                    <a className="nav-link" href="#" onClick={() => handleViewChange('invoice')}>
                                        <i className="bi bi-bar-chart"></i> Invoices
                                    </a>
                                </li>
                            </ul>
                            <hr className="navbar-divider my-5 opacity-20" />
                            <div className="mt-auto"></div>
                            <ul className="navbar-nav">
                                <li className="nav-item">
                                    <a className="nav-link" href="#">
                                        <i className="bi bi-box-arrow-left"></i> Logout
                                    </a>
                                </li>
                            </ul>
                        </div>
                    </div>
                </nav>
                {/* Main content and Invoice creation */}
                {view === 'dashboard' ? (
                    <div className="h-screen flex-grow-1 overflow-y-lg-auto">
                        {/* Header */}
                        {/* <header className="bg-surface-primary border-bottom pt-6">
                            <div className="container-fluid">
                                <div className="mb-npx">
                                    <div className="row align-items-center">
                                        <div className="col-sm-6 col-12 mb-4 mb-sm-0">
                                            <h1 className="h2 mb-0 ls-tight">Application</h1>
                                        </div>
                                    </div>
                                    <ul className="nav nav-tabs mt-4 overflow-x border-0">
                                    </ul>
                                </div>
                            </div>
                        </header> */}
                        {/* Main */}
                        <main className="py-6 bg-surface-secondary">
                            <div className="container-fluid">
                                <div className="card shadow border-0 mb-7">
                                    <div className="card-header d-flex justify-content-between align-items-center">
                                        <h5 className="mb-0">Applications</h5>
                                        <div className="d-flex">
                                            <input type="text" className="form-control" placeholder="Search..." />
                                            <div className="input-group-append">
                                                <button className="btn btn-primary">
                                                    <i className="fas fa-search"></i>
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="table-responsive">
                                        <table className="table table-hover table-nowrap">
                                            <thead className="thead-light">
                                                <tr>
                                                    <th scope="col">Invoice Date</th>
                                                    <th scope="col">Customer Name</th>
                                                    <th scope="col">Invoice No</th>
                                                    <th scope="col">Amount</th>
                                                    <th></th>
                                                    <th></th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {/* {invoices && invoices.success && invoices.msg.map((invoice, index) => {
                                                const invoiceDate = new Date(invoice.invoiceDate);
                                                const formattedDate = invoiceDate.toLocaleDateString('en-US', {
                                                    year: 'numeric',
                                                    month: 'short',
                                                    day: 'numeric',
                                                });

                                                return (
                                                    <tr key={index}>
                                                        <td>{formattedDate}</td>
                                                        <td>{invoice.customerName}</td>
                                                        <td>{invoice.invoiceNo}</td>
                                                        <td>${invoice.amount.toFixed(2)}</td>
                                                        <td class="text-end"><a href="#" class="btn btn-sm btn-neutral w-20 bg-white h-10">View</a>
                                                    <button type="button" class="btn btn-sm btn-square btn-neutral bg-white  text-danger-hover">
                                                        <i class="bi bi-trash"></i>
                                                    </button></td>
                                                    </tr>
                                                );
                                            })} */}
                                                {renderInvoices()}

                                            </tbody>
                                        </table>
                                    </div>
                                    <div className="card-footer border-0 py-5">
                                        <span className="text-muted text-sm">
                                            {/* Showing {invoices.msg.length} items out of {invoices.msg.length} results found */}
                                            Showing {invoices.msg ? invoices.msg.length : 0} items out of {invoices.msg ? invoices.msg.length : 0} results found
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </main>
                    </div>
                ) : (
                    <div className="h-screen flex-grow-1 overflow-y-lg-auto">
                        <main className="py-6 bg-surface-secondary">
                            <div className="container-fluid">
                                <div className="card shadow border-0 mb-7">
                                    <div className="card-header d-flex justify-content-between align-items-center">
                                        <h5 className="mb-0">Invoice Creation</h5>
                                    </div>
                                    <br></br>
                                    {/* <div class="d-flex align-items-center gap-5">
                                        <div class="btn-group w-25"> 
                                            <button class="btn btn-primary btn-sm bg-light dropdown-toggle text-dark border-1 border-light shadow-none" style={{marginLeft:"15px"}}  type="button" id="defaultDropdown" data-bs-toggle="dropdown" data-bs-auto-close="true" aria-expanded="false">
                                                Select Customer
                                            </button>
                                            <ul class="dropdown-menu" aria-labelledby="defaultDropdown">
                                                <li><a class="dropdown-item" href="#">World wide Web Internet</a></li>
                                                <li><a class="dropdown-item" href="#">Menu item</a></li>
                                                <li><a class="dropdown-item" href="#">Menu item</a></li>
                                            </ul>
                                        </div>
                                        <button className="btn btn-primary bg-light btn-sm text-dark d-flex align-items-center me-2 border-1 border-black shadow-none" onClick={addNewRow}>
                                            <i className="bi bi-plus"></i>
                                            <span className="ms-1">Add Row</span>
                                        </button>
                                    </div> */}
                                    <div className="d-flex align-items-center gap-3">
                                        <div className="btn-group ml-5" style={{ width: "200px" }}>
                                            <button
                                                className="btn btn-primary btn-sm dropdown-toggle text-dark bg-light border-1 border-black shadow-none w-100"
                                                type="button"
                                                id="defaultDropdown"
                                                data-bs-toggle="dropdown"
                                                data-bs-auto-close="true"
                                                aria-expanded="false"
                                            >
                                                Select Customer
                                            </button>
                                            <ul className="dropdown-menu" aria-labelledby="defaultDropdown">
                                                <li><a className="dropdown-item" href="#">Menu item</a></li>
                                                <li><a className="dropdown-item" href="#">Menu item</a></li>
                                                <li><a className="dropdown-item" href="#">Menu item</a></li>
                                            </ul>
                                        </div>
                                        <button
                                            className="btn btn-primary bg-light btn-sm text-dark d-flex align-items-center border-1 border-black shadow-none"
                                            style={{ width: "120px" }}
                                            onClick={addNewRow}
                                        >
                                            <i className="bi bi-plus"></i>
                                            <span className="ms-1">Add Row</span>
                                        </button>
                                    </div>

                                    <br></br>
                                    <div className="table-responsive">
                                        <table className="table table-hover table-nowrap">
                                            <thead className="thead-light">
                                                <tr>
                                                    <th scope="col">Invoice Date</th>
                                                    <th scope="col">Customer Name</th>
                                                    <th scope="col">Invoice No</th>
                                                    <th scope="col">Amount</th>
                                                    <th></th>
                                                    <th></th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {/* {invoices && invoices.success && invoices.msg.map((invoice, index) => {
                                                const invoiceDate = new Date(invoice.invoiceDate);
                                                const formattedDate = invoiceDate.toLocaleDateString('en-US', {
                                                    year: 'numeric',
                                                    month: 'short',
                                                    day: 'numeric',
                                                });

                                                return (
                                                    <tr key={index}>
                                                        <td>{formattedDate}</td>
                                                        <td>{invoice.customerName}</td>
                                                        <td>{invoice.invoiceNo}</td>
                                                        <td>${invoice.amount.toFixed(2)}</td>
                                                        <td class="text-end"><a href="#" class="btn btn-sm btn-neutral w-20 bg-white h-10">View</a>
                                                    <button type="button" class="btn btn-sm btn-square btn-neutral bg-white  text-danger-hover">
                                                        <i class="bi bi-trash"></i>
                                                    </button></td>
                                                    </tr>
                                                );
                                            })} */}
                                                {renderInvoices()}

                                            </tbody>
                                        </table>
                                    </div>
                                    <div className="card-footer border-0 py-5">
                                        <span className="text-muted text-sm">
                                            {/* Showing {invoices.msg.length} items out of {invoices.msg.length} results found */}
                                            Showing {invoices.msg ? invoices.msg.length : 0} items out of {invoices.msg ? invoices.msg.length : 0} results found
                                        </span>
                                    </div>
                                </div>
                            </div>
                        </main>
                    </div>
                    // <main class="py-6 bg-surface-secondary">
                    //     <div class="container-fluid">

                    //         <div class="card shadow border-0 mb-7">
                    //             <div class="card-header">
                    //                 <h5 class="mb-0">Invoice Creation</h5>
                    //             </div>
                    //             <br></br>
                    //             {/* <div className="d-flex align-items-center gap-5">
                    //                     <div class="btn-group w-25">
                    //                         <button class="btn btn-primary btn-sm dropdown-toggle text-dark bg-light border-1 border-black shadow-none" style={{ marginLeft: "15px" }} type="button" id="defaultDropdown" data-bs-toggle="dropdown" data-bs-auto-close="true" aria-expanded="false">
                    //                             Select Customer
                    //                         </button>
                    //                         <ul class="dropdown-menu" aria-labelledby="defaultDropdown">
