import React from 'react';
//import "../components/Template.css"
import logo from '../components/images/logo-png.png';
import '@fortawesome/fontawesome-free/css/all.min.css';

const Template = React.forwardRef(({ invoiceData }, ref) => {
    const styles = {
        invoice: {
            background: '#fff',
            padding: '20px',
        },
        invoiceCompany: {
            fontSize: '15px',
            textAlign: 'center',
        },
        companyDetail: {
            fontSize: '10px',
            textAlign: 'center',
            wordWrap: 'break-word',
            whiteSpace: 'normal',
        },
        invoiceHeader: {
            margin: '0 -45px',
            background: '#f0f3f4',
            padding: '8px',
        },
        customerDetails: {
            fontSize: '10px',
        },
        invoiceDate: {
            textAlign: 'right',
            paddingLeft: '20px',
        },
        invoiceTo: {
            paddingRight: '10px',
        },
        invoicePrice: {
            background: '#f0f3f4',
            display: 'table',
            width: '100%',
        },
        invoicePriceLeft: {
            display: 'table-cell',
            padding: '20px',
            fontSize: '20px',
            fontWeight: '600',
            width: '75%',
            position: 'relative',
            verticalAlign: 'middle',
        },
        subPrice: {
            display: 'table-cell',
            verticalAlign: 'middle',
            padding: '0 20px',
        },
        smallText: {
            fontSize: '12px',
            fontWeight: '400',
            display: 'block',
        },
        invoicePriceRow: {
            display: 'table',
            float: 'left',
        },
        invoicePriceRight: {
            width: '25%',
            background: '#2d353c',
            color: '#fff',
            fontSize: '28px',
            textAlign: 'right',
            verticalAlign: 'bottom',
            fontWeight: '300',
        },
        invoicePriceRightSmall: {
            display: 'block',
            opacity: '0.6',
            position: 'absolute',
            top: '10px',
            left: '10px',
            fontSize: '12px',
        },
        invoiceFooter: {
            borderTop: '1px solid #ddd',
            paddingTop: '10px',
            fontSize: '10px',
        },
        invoiceNote: {
            color: '#999',
            marginTop: '80px',
            fontSize: '85%',
        },
        tableInvoice: {
            width: '100%',
        },
        tableHeader: {
            backgroundColor: '#f0f3f4',
            fontWeight: 'bold',
        },
        textCenter: {
            textAlign: 'center',
        },
        textRight: {
            textAlign: 'right',
        },
        textInverse: {
            color: '#2d353c',
        },
        fW300: {
            fontWeight: '300',
        },
        fW600: {
            fontWeight: '600',
        },
        mT5: {
            marginTop: '5px',
        },
        mB5: {
            marginBottom: '5px',
        },
        mR10: {
            marginRight: '10px',
        },
    };
    return(
    <div ref={ref} class="container">
        <div class="col-md-12">
            <div class="invoice">
                <div class="">
                    <div class="invoice-company text-inverse f-w-300">
                        {invoiceData[0].companyDetails.companyName}
                    </div>
                    <div className='company-detail'>
                        {invoiceData[0].companyDetails.companyaddress},{invoiceData[0].companyDetails.companycity}, {invoiceData[0].companyDetails.companystate}, Phone No: {invoiceData[0].companyDetails.companyphoneNo}
                    </div>
                </div>
                <div class="invoice-header">
                    <div class="invoice-to customerDetails">
                        <small>to</small>
                        <address class="m-t-5 m-b-5 ">
                            {invoiceData[0].companyDetails.customerdetails[0].customername}<br />
                            {invoiceData[0].companyDetails.customerdetails[0].custoomeraddress}
                            <br />
                            {invoiceData[0].companyDetails.customerdetails[0].customercity}, {invoiceData[0].companyDetails.customerdetails[0].customerstate}
                        </address>
